package com.gallagher.nzcovidpass;

import static org.junit.Assert.assertThrows;

import androidx.annotation.NonNull;

import org.junit.Assert;
import org.junit.Test;
import org.junit.function.ThrowingRunnable;

import java.util.Collections;
import java.util.Date;

public class PassVerifierTests {
    @NonNull
    private static final String validPassPayload = "NZCP:";

    @NonNull
    private final PassVerifier verifier = new PassVerifier(Collections.singletonList(WellKnownIssuerNames.NZCP_TEST));
    // tests run against a fixed "now" time to stop them failing when run in the future
    @NonNull
    private final Date referenceTime = new Date(1639345844000L);

    // SUCCESS test
    @Test
    public void testValidatesTrustedIssuer() throws PassVerificationError, CwtSecurityTokenValidationError, CwtSecurityTokenError {
        verifier.verify(validPassPayload, referenceTime);
    }

    @Test
    public void testRejectsIncorrectPayload() {
        // some other thing that might be in a QR code
        assertThrows("", PassVerificationError.InvalidPrefix.class, () -> {
            verifier.verify("https://www.example.com", referenceTime);
        });
    }

    @Test
    public void testRejectsIncorrectVersion() {
        // some other thing that might be in a QR code
        assertThrows("", PassVerificationError.InvalidVersion.class, () -> {
            verifier.verify("NZCP:/2/sdfdsfd", referenceTime);
        });
    }

    @Test
    public void testRejectsBadPublicKey() {
        String payload = "NZCP:";
        assertThrows("", CwtSecurityTokenValidationError.InvalidSignature.class, () -> {
            verifier.verify(payload, referenceTime);
        });
    }

    @Test
    public void testRejectsPublicKeyNotFound() {
        String payload = "NZCP:";
        assertThrows("", CwtSecurityTokenValidationError.InvalidIssuer.class, () -> {
            verifier.verify(payload, referenceTime);
        });
    }

    @Test
    public void testRejectsModifiedSignature() {
        String payload = "NZCP:";
        assertThrows("", CwtSecurityTokenValidationError.InvalidSignature.class, () -> {
            verifier.verify(payload, referenceTime);
        });
    }

    @Test
    public void testRejectsModifiedPayload() {
        String payload = "NZCP:";
        assertThrows("", CwtSecurityTokenValidationError.InvalidSignature.class, () -> {
            verifier.verify(payload, referenceTime);
        });
    }

    @Test
    public void testRejectsExpiredPass() {
        String payload = "NZCP:";
        assertThrows("", CwtSecurityTokenValidationError.Expired.class, () -> {
            verifier.verify(payload, referenceTime);
        });
    }

    @Test
    public void testRejectsNotYetActivePass() {
        String payload = "NZCP:";
        assertThrows("", CwtSecurityTokenValidationError.NotYetValid.class, () -> {
            verifier.verify(payload, referenceTime);
        });
    }

    // these ones I've added, they're not strictly from the nzcp website

    @Test
    public void testRejectsValidPassAsExpired_FutureReferenceDate() {
        long yearSeconds = 31540000;
        Date futureTime = new Date(referenceTime.getTime() + (yearSeconds * 1000) * 10); // sample pass expires in 2031, move past that date
        assertThrows("", CwtSecurityTokenValidationError.Expired.class, () -> {
            verifier.verify(validPassPayload, futureTime);
        });
    }

    @Test
    public void testRejectsValidPassAsNotActive_PastReferenceDate() {
        long yearSeconds = 31540000;
        Date pastTime = new Date(referenceTime.getTime() - (yearSeconds * 1000));
        assertThrows("", CwtSecurityTokenValidationError.NotYetValid.class, () -> {
            verifier.verify(validPassPayload, pastTime);
        });
    }
}
